<?phpclass PositionsController extends AppController {	public $name = 'Positions';	public $helpers = array('Html');	function beforeFilter(){    		parent::beforeFilter();  	}	function index() {		$this->loadModel('Mcategory');				$this->set('Categorylist', $this->Mcategory->find('all'));				$this->set('positions', $this->Position->find('all'));	}    	function edit($id = null) {		$this->loadModel('Mcategory');			$this->set('Categorylist', $this->Mcategory->find('all'));			$this->Position->ID = $id;		if (empty($this->data)) {			$this->data = $this->Position->read();		} else {			if ($this->Position->save($this->data)) {				$this->Session->setFlash('Your Home has been updated.');				$this->redirect(array('action' => 'index'));			}		}   	}	}?>